<div id="container">
	<div id="header">		<?php get_header(); ?>	</div>	<div id="leftsidebar">		<?php include (TEMPLATEPATH . '/leftsidebar.php'); ?>	</div>
	<div id="content">		<?php if (have_posts()) : ?>		<h2 class="pagetitle">Search Results</h2>		<?php next_posts_link('&laquo; Previous Entries') ?>		<?php previous_posts_link('Next Entries &raquo;') ?>		<?php while (have_posts()) : the_post(); ?>		<h3 id="post-<?php the_ID(); ?>">			<a href="<?php the_permalink() ?>" rel="bookmark" title="Permanent Link to <?php the_title(); ?>"><?php the_title(); ?></a>		</h3>		<?php the_time('l, F jS, Y') ?>		<p class="postmetadata">Posted in <?php the_category(', ') ?> | <?php edit_post_link('Edit', '', ' | '); ?>		</p>		<?php endwhile; ?>		<?php next_posts_link('&laquo; Previous Entries') ?>		<?php previous_posts_link('Next Entries &raquo;') ?>		<?php else : ?>		<h2 class="center">No posts found. Try a different search?</h2>		<?php include (TEMPLATEPATH . '/searchform.php'); ?>		<?php endif; ?>	</div>	<div id="footer">		<?php get_footer(); ?>	</div></div>